import React from 'react';
import { ComposedChart, Line, XAxis, YAxis, Label, ResponsiveContainer, Tooltip, Legend, Area, BarChart, Bar } from 'recharts';
import Title from '../dashboard/Title';
import $ from "jquery";
import { withTheme } from '@material-ui/core/styles';
import FormControl from '@material-ui/core/FormControl';
import InputLabel from '@material-ui/core/InputLabel';
import Select from '@material-ui/core/Select';
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";

class VisBarChart extends React.Component{
    constructor() {
        super();
        this.state = {
            data: this.createData(),
            time: new Date(this.convertDate("2020-02-02")),
        }
    }

    createData() {
        let data = []
        let i = 0;
        for( i ; i < 10; i++) {
            data.push({ time:0, quantity:i, firm_prediction:0, IBC_prediction: 0, sd_interval:[10,20]})
        }
        return data;
    }

    componentDidMount() {
        this.update()
    }

    convertDate(str) {
        // console.log(str)
        var date = new Date(str),
            month = ("0" + (date.getUTCMonth() + 1)).slice(-2),
            day = ("0" + (date.getUTCDate())).slice(-2);
            // console.log(day);
        return [date.getFullYear(), month, day].join("-");
    }


    update() {
        $.get("api/select_barchart", (data) =>{
            this.setState({
                data: data,
            })
        });
    }


    render() {

    function formatXAxis(str) {
        // console.log(str)
        var date = new Date(str),
            month = ("0" + (date.getUTCMonth() + 1)).slice(-2),
            day = ("0" + date.getUTCDate()).slice(-2);
        return [date.getFullYear(), month, day].join("-");
    }

    const handleChange_time = date => {
        var pick_time = this.convertDate(date);
        console.log(pick_time)
        $.ajax({
            type: "POST",
            url: "api/select_barchart",
            data: {time: pick_time},
            success: () => this.update(),
            error: () => this.update()
        });
        this.setState({
          time: date
        });
    };

    return (
    console.log(this.state.startDate),
    console.log(this.state.endDate),
    <React.Fragment>
        <FormControl >
        <InputLabel htmlFor="age-native-simple">Model</InputLabel>
        <Select
          native
          value={this.state.model}
          onChange={handleChange_model}
          inputProps={{
            name: 'model',
            id: 'age-native-simple',

          }}
        >
            <option value={'2219'}>2219</option>
            <option value={'W8'}>W8</option>
            <option value={'W10'}>W10</option>
            <option value={'W16'}>W16</option>
            <option value={'W19'}>W19</option>
            <option value={'X8'}>X8</option>
            <option value={'X10'}>X10</option>
            <option value={'X16'}>X16</option>
            <option value={'X19'}>X19</option>
        </Select>
      </FormControl>

      <DatePicker
      selected={this.state.time}
      onChange={handleChange_time}
      dateFormat="MM/yyyy"
      showMonthYearPicker
      showFullMonthYearPicker
    />

      <Title>Quantity vs. Model</Title>
      <ResponsiveContainer>
            <BarChart 
                margin={{
                    top: 16,
                    right: 16,
                    bottom: 0,
                    left: 34,
                }} 
                data={this.state.data}
            >
                <Bar dataKey="quantity" fill="#8884d8" />
            </BarChart>
      </ResponsiveContainer>
    </React.Fragment>
    )
    }
}
export default withTheme(VisBarChart);
