import React from 'react';
import { ComposedChart, Line, XAxis, YAxis, Label, ResponsiveContainer, Tooltip, Legend, Area, BarChart, Bar } from 'recharts';
import Title from '../dashboard/Title';
import $ from "jquery";
import { withTheme } from '@material-ui/core/styles';
import FormControl from '@material-ui/core/FormControl';
import InputLabel from '@material-ui/core/InputLabel';
import Select from '@material-ui/core/Select';
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";

class VisBarChart extends React.Component{
    constructor() {
        super();
        this.state = {
            data: this.createData(),
            startDate: new Date(this.convertDate("2016-01-02")),
            endDate: new Date(this.convertDate("2020-06-02"))
        }
    }

    createData() {
        let data = []
        let i = 0;
        for( i ; i < 10; i++) {
            data.push({ time:0, quantity:i, firm_prediction:0, IBC_prediction: 0, sd_interval:[10,20]})
        }
        return data;
    }

    componentDidMount() {
        this.update()
    }

    convertDate(str) {
        // console.log(str)
        var date = new Date(str),
            month = ("0" + (date.getUTCMonth() + 1)).slice(-2),
            day = ("0" + (date.getUTCDate())).slice(-2);
            // console.log(day);
        return [date.getFullYear(), month, day].join("-");
    }


    update() {
        $.get("api/select_advanced", (data) =>{
            // var start = new Date(this.convertDate(data[0].time));
            // var end = new Date(this.convertDate(data[data.length-1].time));
            this.setState({
                data: data,
                // startDate: start,
                // endDate: end
            })
        });
    }


    render() {
    const handleChange_model = (event) => {
        $.ajax({
            type: "POST",
            url: "api/select_advanced",
            data: {model: event.target.value},
            success: () => this.update(),
            error: () => this.update()
        });
            console.log(event.target.value)

    };

    function formatXAxis(str) {
        // console.log(str)
        var date = new Date(str),
            month = ("0" + (date.getUTCMonth() + 1)).slice(-2),
            day = ("0" + date.getUTCDate()).slice(-2);
        return [date.getFullYear(), month, day].join("-");
    }

    const handleChange_start = date => {
        var start = this.convertDate(date);
        console.log(start)
        $.ajax({
            type: "POST",
            url: "api/select_advanced",
            data: {start_date: start},
            success: () => this.update(),
            error: () => this.update()
        });
        this.setState({
          startDate: date
        });
    };

    const handleChange_end = date => {
        var end = this.convertDate(date);
        console.log(end)
        $.ajax({
            type: "POST",
            url: "api/select_advanced",
            data: {end_date: end},
            success: () => this.update(),
            error: () => this.update()
        });
        this.setState({
          endDate: date
        });
    };

    const data = [
        {
            name: 'Page A', uv: 4000, pv: 2400, amt: 2400,
        },
        {
            name: 'Page B', uv: 3000, pv: 1398, amt: 2210,
        },
        {
            name: 'Page C', uv: 2000, pv: 9800, amt: 2290,
        },
        {
            name: 'Page D', uv: 2780, pv: 3908, amt: 2000,
        },
        {
            name: 'Page E', uv: 1890, pv: 4800, amt: 2181,
        },
        {
            name: 'Page F', uv: 2390, pv: 3800, amt: 2500,
        },
        {
            name: 'Page G', uv: 3490, pv: 4300, amt: 2100,
        },
    ];

    return (
    console.log(this.state.startDate),
    console.log(this.state.endDate),
    <React.Fragment>
        <FormControl >
        <InputLabel htmlFor="age-native-simple">Model</InputLabel>
        <Select
          native
          value={this.state.model}
          onChange={handleChange_model}
          inputProps={{
            name: 'model',
            id: 'age-native-simple',

          }}
        >
            <option value={'2219'}>2219</option>
            <option value={'W8'}>W8</option>
            <option value={'W10'}>W10</option>
            <option value={'W16'}>W16</option>
            <option value={'W19'}>W19</option>
            <option value={'X8'}>X8</option>
            <option value={'X10'}>X10</option>
            <option value={'X16'}>X16</option>
            <option value={'X19'}>X19</option>
        </Select>
      </FormControl>

      <>
        <DatePicker
            selected={this.state.startDate}
            onChange={handleChange_start}
            selectsStart
            startDate={this.state.startDate}
            endDate={this.state.endDate}
            dateFormat="MM/yyyy"
            showMonthYearPicker
        />
        <DatePicker
            selected={this.state.endDate}
            onChange={handleChange_end}
            selectsEnd
            startDate={this.state.startDate}
            endDate={this.state.endDate}
            dateFormat="MM/yyyy"
            showMonthYearPicker
        />
        </>

      <Title>Quantity vs. Date</Title>
      <ResponsiveContainer>
            <BarChart width={150} height={40} data={data}>
                <Bar dataKey="uv" fill="#8884d8" />
            </BarChart>
      </ResponsiveContainer>
    </React.Fragment>
    )
    }
}
export default withTheme(VisBarChart);
