import React from 'react';
import $ from "jquery";

import clsx from "clsx";
import CssBaseline from "@material-ui/core/CssBaseline";
import AppBar from "@material-ui/core/AppBar";
import Toolbar from "@material-ui/core/Toolbar";
import IconButton from "@material-ui/core/IconButton";
import MenuIcon from "@material-ui/icons/Menu";
import Typography from "@material-ui/core/Typography";
import Drawer from "@material-ui/core/Drawer";
import ChevronLeftIcon from "@material-ui/icons/ChevronLeft";
import List from "@material-ui/core/List";
import {mainListItems} from "../dashboard/listItems";
import Container from "@material-ui/core/Container";
import TextField from '@material-ui/core/TextField';
import Paper from "@material-ui/core/Paper";
import InputLabel from "@material-ui/core/InputLabel";
import Select from "@material-ui/core/Select";
import FormControl from "@material-ui/core/FormControl";
import Button from '@material-ui/core/Button';
import { compose } from 'recompose';
import {withStyles} from'@material-ui/core';



const drawerWidth = 240;


const styles = theme => ({
  root: {
    display: 'flex',
  },
  toolbar: {
    paddingRight: 24, // keep right padding when drawer closed
  },
  toolbarIcon: {
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'flex-end',
    padding: '0 8px',
    ...theme.mixins.toolbar,
  },
  appBar: {
    zIndex: theme.zIndex.drawer + 1,
    transition: theme.transitions.create(['width', 'margin'], {
      easing: theme.transitions.easing.sharp,
      duration: theme.transitions.duration.leavingScreen,
    }),
  },
  appBarShift: {
    marginLeft: drawerWidth,
    width: `calc(100% - ${drawerWidth}px)`,
    transition: theme.transitions.create(['width', 'margin'], {
      easing: theme.transitions.easing.sharp,
      duration: theme.transitions.duration.enteringScreen,
    }),
  },
  menuButton: {
    marginRight: 36,
  },
  menuButtonHidden: {
    display: 'none',
  },
  title: {
    flexGrow: 1,
  },
  drawerPaper: {
    position: 'relative',
    whiteSpace: 'nowrap',
    width: drawerWidth,
    transition: theme.transitions.create('width', {
      easing: theme.transitions.easing.sharp,
      duration: theme.transitions.duration.enteringScreen,
    }),
  },
  drawerPaperClose: {
    overflowX: 'hidden',
    transition: theme.transitions.create('width', {
      easing: theme.transitions.easing.sharp,
      duration: theme.transitions.duration.leavingScreen,
    }),
    width: theme.spacing(7),
    [theme.breakpoints.up('sm')]: {
      width: theme.spacing(9),
    },
  },
  appBarSpacer: theme.mixins.toolbar,
  content: {
    flexGrow: 1,
    height: '100vh',
    overflow: 'auto',
  },
  container: {
    paddingTop: theme.spacing(4),
    paddingBottom: theme.spacing(4),
  },
  paper: {
    padding: theme.spacing(4),
    //display: 'flex',
    overflow: 'auto',
    //flexDirection: 'column',
  },
  fixedHeight: {
    height: 240,
  },
    finishButton:{
      paddingLeft: theme.spacing(130)
    },
    textField: {
    marginLeft: theme.spacing(1),
    marginRight: theme.spacing(1),
    width: '25ch',
  },
});



class Add extends React.Component{
    constructor() {
        super()
        this.state = {
            open : true,
            id: 0,
            model: 'NULL',
            time: 0,
            historical:'NULL',
            firm_prediction:'NULL',
            IBC_prediction:'NULL'
        }
    }



    addNew(){
        $.ajax({
                type: "POST",
                url: "api/add",
                data: {id:this.state.id,
                model:this.state.model,
                time:this.state.time,
                historical:this.state.historical,
                firm_prediction: this.state.firm_prediction,
                IBC_prediction: this.state.IBC_prediction},
            });
    }




    render() {
        const id = (event) => {
            this.setState({id:event.target.value})
        }


        const handleChange = (event) => {
            this.setState({model:event.target.value})
            //console.log(event.target.value)
        };

        const time = (event) =>{
            this.setState({time:event.target.value})
        }

        const hist = (event) =>{
            this.setState({historical:event.target.value})
        }

        const firm = (event) =>{
            this.setState({firm_prediction:event.target.value})
        }

        const IBC = (event) => {
            this.setState({IBC_prediction:event.target.value})
        }

        const handleDrawerOpen = () => {
            this.setState({open:true});
        };
        const handleDrawerClose = () => {
            this.setState({open:false})
        };

        const { classes } = this.props;

        return (
            <div className={classes.root}>
                <CssBaseline />

                <AppBar position="absolute" color="primary" className={clsx(classes.appBar, this.state.open && classes.appBarShift)}>

                    <Toolbar className={classes.toolbar}>
                        <IconButton
                            edge="start"
                            color="inherit"
                            aria-label="open drawer"
                            onClick={handleDrawerOpen}
                            className={clsx(classes.menuButton, this.state.open && classes.menuButtonHidden)}
                        >
                            <MenuIcon />
                        </IconButton>
                        <Typography component="h1" variant="h6" color="inherit" noWrap className={classes.title}>
                            Raw Data
                        </Typography>
        </Toolbar>
      </AppBar>

      <Drawer
        variant="permanent"
        classes={{
          paper: clsx(classes.drawerPaper, !this.state.open && classes.drawerPaperClose),
        }}
        open={this.state.open}
      >
        <div className={classes.toolbarIcon}>
          <IconButton onClick={handleDrawerClose}>
            <ChevronLeftIcon />
          </IconButton>
        </div>
        <List>
            <Link color="primary" href="/">
                <ListItem button>
                    <ListItemIcon>
                        <DashboardIcon />
                    </ListItemIcon>
                    <ListItemText primary="Dashboard" />
                </ListItem>
            </Link>

            <Link color="primary" href="/raw">
                <ListItem button >
                    <ListItemIcon>
                        <StorageIcon />
                    </ListItemIcon >
                    <ListItemText primary="Raw Data" />
                </ListItem>
            </Link>

            <ListItem button onClick={handleClick}>
              <ListItemIcon>
                <VisualIcon />
              </ListItemIcon>
              <ListItemText primary="Visualization" />
              {open_nest ? <ExpandLess /> : <ExpandMore />}
            </ListItem>
            <Collapse in={open_nest} timeout="auto" unmountOnExit>
